package nl.rijksoverheid.minienw.travelvalidation.validationservice.api.data.initialize

import com.fasterxml.jackson.annotation.JsonProperty

/**
* 3.10.2.2.3 Request Body
* The request body is a JSON structure which defines the user generated pubkey, the used key type and the subject.
 * NB Subject dropped as it is in the Airline Traveller/Trip Token.
*/
class ValidationInitializeRequestBody
    (
    /**
     * TODO why is this not a JWK so that the algorithm is specfied?
    * ECDSA or RSA-PSS Key String var Key generated by the user/wallet(x.509 PEM)
    */
    @JsonProperty("pubKey")
    var walletPublicKey : String?,

    /**
    * ES256/PS256 /RS256 String Use Crypto Method according to JWT definitions in RFC75182 callback e.g.https://... String Optional Callback URL
     * */
    @JsonProperty("alg")
    var walletPublicKeyAlgorithm : String?,

    /**
    * TODO careful not to double encode requests/responses
    * Random 16 bytes, Base64 encoded
    */
    var nonce : String?,
)

